import React from 'react';
import Footer from '../components/footer';

const customStyle = {
  position: 'absolute',
  top: '50%',
  right: '50%',
  width: '110%', // Adjust size as needed
  height: '110%', // Adjust size as needed
  backgroundColor: '#fcd137', // This is the yellow color
  borderRadius: '1rem', // Adjust for rounded corners
  transform: 'translate(50%, -50%)', // This will center it correctly
  zIndex: '-1', // This will ensure it's in the background
};

const LoginPage = () => {
  return (
    <div className="flex flex-col min-h-screen">
      <div className="flex flex-grow">
        <div className="flex flex-col justify-center w-1/2 pl-8">
          <h1 className="text-left text-5xl pl-4 font-bold mb-4">Volunteer Management System</h1>
        </div>
        <div style={customStyle}></div>
        {/* Adjust the width of this div to control the form size */}
        <div className="flex flex-col justify-center w-1/3 pr-8">
          <form className="bg-clip-border p-7 pt-14 pl-12 pr-12 bg-[#fef6d8] rounded-3xl shadow-2xl flex flex-col items-center">
            <div className="mb-4 w-full">
              <input className="w-full h-14 px-4 py-3 border rounded-xl text-center" type="email" placeholder="Email" />
            </div>
            <div className="mb-4 w-full">
              <input className="w-full h-14 px-4 py-3 border rounded-xl text-center" type="password" placeholder="Password" />
            </div>
            <div className="flex justify-between mb-4 w-full">
              <button className="w-1/2 bg-[#fcd137] hover:bg-green-700 text-black font-bold py-3 px-4 rounded-2xl mr-2">Log In</button>
              <button className="w-1/2 bg-[#fcd137] hover:bg-green-700 text-black font-bold py-3 px-4 rounded-2xl ml-2">Sign Up</button>
            </div>
            <a className="self-start text-slate-400 mb-10 pl-4 " href="#">Forgot Password?</a>
          </form>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default LoginPage;
